import React from 'react';
import Header from './Header.js';
import Footer from './Footer.js';
import Main from './Main.js';
import PopupWithForm from './PopupWithForm.js';
import ImagePopup from './ImagePopup.js';


function App() {
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
    const [selectedCard, setSelectedCard] = React.useState(undefined);

    function handleEditAvatarClick() {
        setIsEditAvatarPopupOpen(true);
    }

    function handleEditProfileClick() {
        setIsEditProfilePopupOpen(true);
    }

    function handleAddPlaceClick() {
        setIsAddPlacePopupOpen(true);
    }

    function handleClickCard(card) {
        setSelectedCard(card);
    }

    function closeAllPopups() {
        setIsAddPlacePopupOpen(false);
        setIsEditAvatarPopupOpen(false);
        setIsEditProfilePopupOpen(false);
        setSelectedCard(undefined);
    }
    return (
        <div className='page'>

            <Header />

            <Main
                onEditAvatar={handleEditAvatarClick}
                onEditProfile={handleEditProfileClick}
                onAddPlace={handleAddPlaceClick}
                onCardClick={handleClickCard}
            />

            <Footer />

            <PopupWithForm
                name='profile'
                title='Редактировать профиль'
                isOpen={isEditProfilePopupOpen}
                onClose={closeAllPopups}
                buttonName='Сохранить'>
                <div className="popup__form popup__form_profile" noValidate>
                    <input
                        id="username-input"
                        type="text"
                        name="name"
                        placeholder="Имя"
                        className="popup__input popup__input_type_username"
                        minLength="2"
                        maxLength="40"
                        required />
                    <span className="popup__input-error username-input-error"></span>
                    <input
                        id="text-input"
                        type="text"
                        name="about"
                        placeholder="О себе"
                        className="popup__input popup__input_type_job"
                        minLength="2"
                        maxLength="200"
                        required />
                    <span className="popup__input-error text-input-error"></span>
                </div>
            </PopupWithForm>

            <PopupWithForm
                name='element'
                title='Новое место'
                isOpen={isAddPlacePopupOpen}
                onClose={closeAllPopups}
                buttonName='Создать'>
                <div className="popup__form popup__form_add" noValidate>
                    <input
                        id="name-input"
                        type="text"
                        placeholder="Название"
                        name="name"
                        className="popup__input popup__input_type_title"
                        minLength="2"
                        maxLength="30"
                        required />
                    <span className="popup__input-error name-input-error"></span>
                    <input
                        id="url-input"
                        type="url"
                        placeholder="Ссылка на картинку"
                        name="link"
                        className="popup__input popup__input_type_link"
                        required />
                    <span className="popup__input-error url-input-error"></span>
                </div>
            </PopupWithForm>

            <PopupWithForm
                name='avatar'
                title='Обновить аватар'
                isOpen={isEditAvatarPopupOpen}
                onClose={closeAllPopups}
                buttonName='Сохранить'>
                <div className="popup__form popup__form_avatar" noValidate>
                    <input
                        id="avatar-input"
                        type="url"
                        placeholder="Ссылка на картинку"
                        name="avatar"
                        className="popup__input popup__input_type_avatar"
                        required />
                    <span className="popup__input-error avatar-input-error"></span>
                </div>
            </PopupWithForm>

            <PopupWithForm
                name='delete'
                title='Вы уверены?'
                onClose={closeAllPopups}
                buttonName='Да'
            >
            </PopupWithForm>

            <ImagePopup
                onClose={closeAllPopups}
                card={selectedCard}
            />

        </div>
    );
}

export default App;
